import {Component, Input} from '@angular/core';
import {CommandModel} from '../../model/command';

@Component({
  selector: 'app-command',
  templateUrl: './command.component.html',
  styleUrls: ['./command.component.css']
})
export class CommandComponent {

  @Input()
  command: CommandModel | undefined;

  constructor(){}

}
<div class="container">
  <div class="card" >
    <div class="content">
      <ul>
        <h3 routerLink="/commande/{{command?.numeroCommande}}">{{command?.dateCommande}} {{command?.dateCommande}}</h3>
        <li>Nombre de cartes: {{command?.nbCarte}} </li>
        <li>Taux de participation: {{command?.tauxParticipation}} </li>

      </ul>
    </div>
    <button type="button" class="btn btn-outline-primary" routerLink="/commande/{{command?.numeroCommande}}">Parametrer</button>
  </div>
</div>
