import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import Validation from "./utils/Validation";
import {AuthService} from "../auth.service";


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{
   signUpForm!: FormGroup;
  formSubmitted: boolean = false

  constructor(private fb: FormBuilder,
              private authSerivce: AuthService){};

  ngOnInit(): void {
    this.signUpForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      noSiren: ['', [Validators.required]],
      password: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(40)]],
      confirmPassword: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(40)]]
      },{
        validators: [Validation.match('password','confirmPassword')]
      });
  }

  onSubmit() {
    this.formSubmitted = true;

    if (this.signUpForm.invalid) {
      return;
    }

    const { email, noSiren, password } = this.signUpForm.value;

    this.authSerivce.register(email, password, noSiren).subscribe(
      (response) => {
        // Handle successful registration (e.g., navigate to another page)
        console.log('Registration successful:', response);
      },
      (error) => {
        // Handle registration error (e.g., display an error message)
        console.error('Registration error:', error);
      }
    );

  }

}
<div class="main">
  <div class="container">
    <div class="title">Inscription</div>
    <p>Veuillez remplir les informations suivantes</p>
    <div class="content">
      <form [formGroup]="signUpForm" (ngSubmit)="onSubmit()">
        <div class="user-details">

          <div class="input-box">
            <label for="email">Email</label>
            <input type="text" id="email" formControlName="email" placeholder="Entrez votre adresse e-mail" required>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['email'].errors?.['required']">Email obligatoire</div>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['email'].errors?.['email']">Email invalide</div>
          </div>

          <div class="input-box">
            <label for="noSiren">N°SIREN</label>
            <input type="text" id="noSiren" formControlName="noSiren" placeholder="Entrez votre n° SIREN" required>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['noSiren'].errors?.['required']">N°SIREN obligatoire</div>

          </div>
          <div class="input-box">
            <label for="password">Password</label>
            <input type="password" id="password" formControlName="password" placeholder="Entrez votre mot de passe" required>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['password'].errors?.['required']">Mot de passe obligatoire</div>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['password'].errors?.['minlength']">6 caractères minimum</div>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['password'].errors?.['maxlength']">40 caractères maximum</div>
          </div>

          <div class="input-box">
            <label for="confirmPassword">Confirmez votre mot de passe</label>
            <input type="password" id="confirmPassword" formControlName="confirmPassword" placeholder="Confirmez votre mot de passe" required>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['confirmPassword'].errors?.['required']">Confirmation de mot de passe obligatoire</div>
            <div class="error-form" *ngIf="formSubmitted && signUpForm.controls['confirmPassword'].errors?.['matching']">Les mots de passe ne sont pas identiques</div>
          </div>

        </div>

        <div class="button">
          <button class ="registerButton" type="submit" >S'inscrire</button>
        </div>

      </form>
    </div>
  </div>
</div>
