import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import Validation from "../../utils/Validation";


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{
   signUpForm!: FormGroup;

/*  signUpForm: FormGroup = new FormGroup({
    email: new FormControl(''),
    password: new FormControl(''),
    confirmPassword: new FormControl(''),
    acceptTerms: new FormControl(false),
  });*/

  formSubmitted: boolean = false

  constructor(private fb: FormBuilder){};

  ngOnInit(): void {
    this.signUpForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      noSiren: ['', [Validators.required]],
      password: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(40)]],
      confirmPassword: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(40)]]
      },{
        validators: [Validation.match('password','confirmPassword')]
      });
  }

  onSubmit() {
    this.formSubmitted = true;

    if (this.signUpForm.invalid) {
      return;
    }

    console.log(JSON.stringify(this.signUpForm.value, null, 2));
  }

  onReset(): void {
    this.formSubmitted = false;
    this.signUpForm.reset();
  }
}
<div class="main">
  <div class="container">
    <div class="title">Inscription</div>
    <p>Veuillez remplir les informations suivantes</p>
    <div class="content">
      <form [formGroup]="signUpForm" (ngSubmit)="onSubmit()">
        <div class="user-details">
          <div class="input-box">
            <label for="email">Email</label>
            <input type="text" id="email" formControlName="email" placeholder="Entrez votre adresse e-mail" required>
            <div *ngIf="formSubmitted && signUpForm.controls['email'].errors" class="invalid-feedback">
              <div *ngIf="signUpForm.controls['email'].errors['required']">Email obligatoire</div>
              <div *ngIf="signUpForm.controls['email'].errors['email']">Email invalide</div>
            </div>
          </div>
          <div class="input-box">
            <label for="noSiren">N°SIREN</label>
            <input type="text" id="noSiren" formControlName="noSiren" placeholder="Entrez votre n° SIREN" required>
            <div *ngIf="formSubmitted && signUpForm.controls['noSiren'].errors" class="invalid-feedback">
              <div *ngIf="signUpForm.controls['noSiren'].errors['required']">N°SIREN obligatoire</div>
              <div *ngIf="signUpForm.controls['noSiren'].errors['noSiren']">N°SIREN invalide</div>
            </div>

          </div>
          <div class="input-box">
            <label for="password">Password</label>
            <input type="password" id="password" formControlName="password" placeholder="Entrez votre mot de passe" required>
            <div *ngIf="formSubmitted && signUpForm.controls['noSiren'].errors" class="invalid-feedback">
              <div *ngIf="signUpForm.controls['password'].errors['required']">Password is required</div>
              <div *ngIf="signUpForm.controls['password'].errors['minlength']">
                6 caractères minimum
              </div>
              <div *ngIf="signUpForm.controls['password'].errors?.maxlength">
                40 caractères maximum
              </div>
            </div>
          </div>
          <div class="input-box">
            <label for="confirmPassword">Confirm Password</label>
            <input type="password" id="confirmPassword" formControlName="confirmPassword" placeholder="Confirmez votre mot de passe" required>
          </div>
        </div>
        <div class="button">
          <button class ="registerButton" type="submit" [disabled]="!signUpForm.valid" >S'inscrire</button>
        </div>
      </form>
    </div>
  </div>
</div>


